import React, { Component } from "react";

class ProductForm extends Component {
  state = {
    nouveauProduct: ""
  };

  handleChange = event => {
    this.setState({ nouveauProduct: event.currentTarget.value });
  };

  handleSubmit = event => {
    event.preventDefault();

    const id = new Date().getTime();
    const nom = this.state.nouveauProduct;

    this.props.onProductAdd({ id, nom });

    this.setState({ nouveauProduct: "" });
  };

  render() {
    return (
      <form onSubmit={this.handleSubmit}>
        <input
          value={this.state.nouveauProduct}
          onChange={this.handleChange}
          type="text"
          placeholder="Ajouter un Product"
        />
        <button>Confirmer</button>
      </form>
    );
  }
}

export default ProductForm;
